INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (17,'Royal_Fleet_Auxiliary.png','<p>Two RFA Practice their RIB handling skills</p>',10,'2012-12-10 12:22:18','2012-12-10 12:22:18');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (18,'Capgemini_1.png','<p>Collaboration – it\'s not just the way we work, it’s what we live and breathe.</p>',5,'2012-12-13 09:59:34','2012-12-13 09:59:34');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (19,'Capgemini_2.png','<p>Work – yes, you get full training, but you get real responsibilities too.<br></p>',5,'2012-12-13 10:00:19','2012-12-13 10:00:19');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (20,'Capgemini_3.png','<p>Success – from start to ongoing achievements and results. The successes are endless at Capgemini.</p>',5,'2012-12-13 10:01:08','2012-12-13 10:01:08');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (21,'Charnwood_1.png','<p>Great careers in hospitality with even better training\r\nopportunities</p>\r\n',11,'2012-12-18 09:56:06','2012-12-18 09:56:06');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (22,'Charnwood_2.png','<p>Study for a nationally recognised qualification – without\r\nhaving to go to College or University!</p>\r\n',11,'2012-12-18 09:56:41','2012-12-18 09:56:41');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (23,'Charnwood_3.png','<p>Hospitality apprenticeships. A great way for young people to\r\nstart a career!</p>\r\n',11,'2012-12-18 09:57:15','2012-12-18 09:57:15');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (24,'National_Grid_1.png','<p>\"Hands-on training is by far the best way for me to learn and my mentor has always been happy to show me the ropes and look after me.\"<br></p>',9,'2013-01-08 11:57:38','2013-01-08 11:57:38');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (25,'National_Grid_2.png','<p>\"I want to be developed so one day I can manage a team of my own – I know I can do that here.\" <br></p>',9,'2013-01-08 11:59:16','2013-01-08 11:59:16');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (26,'National_Grid_3.png','<p>\"The company is very keen for us to push on and go further – if you have ambition, you can realise it here.\" <br></p>',9,'2013-01-08 12:00:52','2013-01-08 12:00:52');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (27,'Skills_for_Care_1.png','\"I love meeting people from different\r\nbackgrounds – each person has a story to tell.\"\r\n\r\n',30,'2013-01-08 12:11:04','2013-01-08 12:11:04');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (28,'Skills_for_Care_2.png','\"I can apply my\r\npositive and caring attitude in a hands-on working environment.\"\r\n\r\n',30,'2013-01-08 12:12:08','2013-01-08 12:12:08');
INSERT INTO `employer_photos` (`id`,`photo`,`caption`,`employer_id`,`created_at`,`updated_at`) VALUES (29,'Skills_for_Care_3.png','\"I’m proud of my achievements and\r\nthat I have such a satisfying job.\"\r\n\r\n',30,'2013-01-08 12:13:19','2013-01-08 12:13:19');
