INSERT INTO `training_providers` (`id`,`provider_name`,`region`,`logo_image`,`provider_description`,`email_address`,`web_address`,`contact_number`,`position`,`created_at`,`updated_at`) VALUES (71,'TIR Training',NULL,'TIRlogo_100x100.png','<p>TIR can help you on the road to a career in logistics which\r\nwill span a lifetime.</p><p>We are a leading provider of Apprenticeships to the UK\r\nlogistics sector. <br></p><p>Our experts can help you learn on the job at a pace that\r\nsuits you. <br></p><p>TIR Apprenticeships equip you with valuable vocational and\r\nprofessional skills. If you are already in work, we can enhance your skills to\r\nimprove your career prospects. <br></p><p>We offer Level 2 and Level 3 Apprenticeships in a wide range\r\nof logistics qualifications. <br></p><p>Call us on 01482 213113 to speak to a member of our team.<br></p>','','www.t-i-r.com','01482213113',9,'2013-12-12 12:35:01','2013-12-12 12:35:01');
